--

CREATE TABLE IF NOT EXISTS `think_comment` (
  `comm_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `parent_comm_id` bigint(20) unsigned DEFAULT NULL,
  `article_mood_id` bigint(20) unsigned NOT NULL,
  `user_id` int(10) unsigned NOT NULL,
  `content` text NOT NULL,
  `type` tinyint(3) unsigned NOT NULL,
  `comm_time` int(11) NOT NULL,
  `like` bigint(20) unsigned NOT NULL DEFAULT '0',
  `remove` tinyint(3) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`comm_id`),
  KEY `comm_id` (`comm_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=52 ;

