--
-- 表的结构 `think_daily`
--

CREATE TABLE IF NOT EXISTS `think_daily` (
  `id` bigint(11) NOT NULL AUTO_INCREMENT,
  `content` text NOT NULL,
  `show_time` varchar(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=7 ;

--
-- 转存表中的数据 `think_daily`
--

INSERT INTO `think_daily` (`id`, `content`, `show_time`) VALUES
(1, '<h4>《永遇乐·落日熔金》</h4><p>年代: 宋 作者: 李清照</p><p>落日熔金，暮云合璧，人在何处。</p><p>染柳烟浓，吹梅笛怨，春意知几许。</p><p>元宵佳节，融和天气，次第岂无风雨。</p><p>来相召、香车宝马，谢他酒朋诗侣。</p><p>中州盛日，闺门多暇，记得偏重三五。</p><p>铺翠冠儿，捻金雪柳，簇带争济楚。</p><p>如今憔悴，风鬟霜鬓，怕见夜间出去。</p><p>不如向、帘儿底下，听人笑语。</p>', '2018-06-06'),
(3, '墙头马上遥相顾，一见知君即断肠。', '2018-06-07'),
(4, '半世浮萍随逝水，一宵冷雨葬名花。', '2018-06-08'),
(5, '<p>庭院深深深几许，杨柳堆烟，帘幕无重数。</p><p>玉勒雕鞍游冶处，楼高不见章台路。</p><p>雨横风狂三月暮，门掩黄昏，无计留春住。</p><p>泪眼问花花不语，乱红飞过秋千去。</p>\n<p>——欧阳修《蝶恋花·庭院深深深几许》</p>', '2018-06-09'),
(6, '山中何事，\n松花酿酒，\n春水煎茶。', '');

