INSERT INTO `think_fragment` (`fragment_id`, `user_id`, `tag`, `content`, `like`, `skip`, `img`, `create_time`, `remove`) VALUES
(1, 1, 1, '     我回忆起了很多以前的事情但是不能和你说不能和你打感情牌不爱就是不爱了你再也不愿回忆以前的事情你吃鱼吃虾吃竹笋吃榴莲等等的时候你看到莲的时候你会想起我吗', 40, 1, '', 1506666666, 0),
(2, 2, 2, '每个人的一生都在演绎一幕又一幕的戏，或真或假，或长或短，或喜或悲。你在这场戏里扮演的那个我，我在那场戏里扮演这个你，各自微笑，各自流泪。 一场戏的结束意味着另一场戏的开始，所以我们不必过于沉浸在昨天。记住也好，忘记也罢，生命本是场轮回，来来去去，何曾有过丝毫的停歇', 87, 5, '', 1408852918, 0),
(3, 1, 3, '你给的爱，沃野千里。长出漫山的幸福，还有一望无际的温存。', 30, 0, '', 1454838400, 0),
(4, 3, 4, '以后要是能有自己的房子 希望有个阁楼和大大的露台 阁楼上有落地窗 阳光照进来 晒着太阳 雨天 听着雨声拍拍打打 露台上放着我的花花草草 还有一个种满小青菜的大花盆 再摆上一张桌子两个摇椅 周末叫上好友 喝茶吃点心相聚一下午', 45, 0, '', 1332427715, 0),
(5, 6, 5, '到处都有痛苦，而比痛苦更为持久且尖利伤人的是，到处都有抱有期望的等待。——约翰·伯格', 41, 25, '', 1567890864, 0),
(6, 5, 5, '有时在追逐中得到，有时在追逐中迷失。', 67, 2, '', 1478901234, 0),
(7, 4, 3, '我用什么才能留住你？ \r\n我给你贫穷的街道、绝望的日落、破败郊区的月亮。 \r\n我给你一个久久地望着孤月的人的悲哀。', 2, 1, '', 1509177657, 0),
(8, 5, 5, '井边明月。\r\n\r\n旷野繁星。\r\n\r\n酒红窗帘你的脸。', 2, 0, '', 1509177821, 0),
(9, 1, 1, '“ 岁月漫长 要心地善良 ” ​​​', 0, 0, '', 1510055103, 0),
(10, 1, 4, '“我也不需要你解释 我一直相信你是对的”《天堂电影院》', 1, 1, '', 1510056488, 0),
(11, 1, 4, '以声之色，塑以花之形　　——《声之形》', 0, 0, '', 1510056536, 0),
(12, 1, 4, '“小八，帕克不会回来了”“但小八要是想等他，就继续等吧” ——《忠犬八公的故事》', 2, 2, '', 1510056661, 0),
(13, 1, 1, '你是晴空万里的蓝天白云你是随风摇摆的山野苍茫你是我永远追不上的空岛《采歌三行情诗》', 2, 6, '', 1510416383, 0),
(14, 7, 2, '我是漫天昏黑的十方弃雨你是暗尘里飘荡的缝隙――大乔小乔《饮酒》', 0, 1, 'bb/252126b45170f840455eaf03f0a6ad.jpg', 1510475584, 0),
(15, 1, 3, '如果说你是海上的烟火 我是浪花的泡沫某一刻 你的光照亮了我', 0, 0, '0c/fc8cb3722112b721a4ffc9f447fd86.jpg', 1510483407, 0),
(16, 1, 2, 'eedd', 0, 0, '67/6a68242fdf63443491f99566fac160.jpg', 1511268018, 0),
(17, 1, 1, 'test', 0, 0, '', 1514204885, 0),
(18, 1, 2, 'dedede', 1, 4, '', 1514207241, 0),
(19, 2, 5, '消息推送测试的碎片1', 0, 4, '', 1514451342, 0),
(20, 2, 3, '消息推送的测试2 带图的', 0, 3, 'f2/63ef2808989d3cbda72f49a7036698.jpg', 1514451395, 0),
(21, 1, 1, '这个灯好漂亮呀', 0, 15, 'd7/cf7c5349c9da2a446a56f7baf21d25.jpg', 1515129270, 0),
(22, 1, 3, '发布测试', 0, 0, '', 1526188512, 0),
(23, 1, 3, '人人尽说江南好，游人只合江南老。春水碧于天，画船听雨眠。\r\n垆边人似月，皓腕凝霜雪。未老莫还乡，还乡须断肠。\r\n\r\n——韦庄《菩萨蛮》其二', 0, 2, '5f/ea3e0215b9808bbdf779b200a90c53.jpg', 1528288697, 0);

